mapper.select:
SELECT COUNT(*) FROM favorites
WHERE user_id = #{userId}
	AND table_name = 'tour'

SELECT COUNT(*) FROM favorites
WHERE user_id = #{userId}
	AND table_name = 'festival'

SELECT *
	FROM favorites
 WHERE user_id = #{userId}
	AND target_no = #{targetNo}
	AND table_name = #{tableName}

mapper.delete:
DELETE FROM favorites
WHERE user_id = #{userId}
AND target_no = #{targetNo}
AND table_name = #{tableName}
