mapper.select:
SELECT A.*,B.* FROM ( SELECT * FROM ( SELECT ROWNUM as rnum, tt2.* FROM ( SELECT t1.festa_no AS festaNo, t1.name AS name, t1.views AS views, TO_CHAR(t1.start_date,'YYYY-MM-DD') AS startDate, TO_CHAR(t1.end_date,'YYYY-MM-DD') AS endDate, t3.image_url AS imageUrl, t3.save_name AS saveName, t2.region_sido AS regionSido FROM festival t1 JOIN region t2 ON t1.region_no = t2.region_no JOIN image t3 ON t1.festa_no = t3.target_no WHERE t3.table_name='festival'
AND t2.region_sido = #{sido}
AND t1.start_date <= #{date} AND t1.end_date >= #{date}
ORDER BY t1.end_date DESC ) tt2
WHERE ROWNUM <= (#{dto.pageSize} * (#{dto.pageNo} - 1) + #{dto.pageSize}) )tt3 WHERE RNUM >= (#{dto.pageSize} * (#{dto.pageNo} - 1) + 1) )A CROSS JOIN( SELECT COUNT(*) AS totalCnt FROM festival t1 JOIN region t2 ON t1.region_no = t2.region_no JOIN image t3 ON t1.festa_no = t3.target_no WHERE t3.table_name='festival'
AND t2.region_sido = #{sido}
AND t1.start_date <= #{date} AND t1.end_date >= #{date}
)B

mapper.update:
UPDATE festival
name=#{name}, subtitle=#{subtitle}, contents=#{contents}, address=#{address}, tel=#{tel}, fee=#{fee}, region_no=#{regionNo}, start_date=#{startDate}, end_date=#{endDate},
WHERE festa_no = #{festaNo}
